import { ExclamationCircleOutlined, SearchOutlined } from '@ant-design/icons'
import { Button, Form, Image, Input, InputNumber, InputRef, message, Modal, Select, Space, Table, Tag } from 'antd'
import type { ColumnsType } from 'antd/es/table'
import { useEffect, useRef, useState } from 'react'
import { getKindAllListAPI, IKind } from '../Kind/service'
import QuantityControl from './components/QuantityControl'
import { deleteInventoryAPI, addOrEditInventoryAPI, getInventoryListAPI, IInventory } from './service'

type SearchParams = Partial<{
  queryString: string
  status: number
  kindId: number
}>

const initialPageQuery = {
  pageNum: 1,
  pageSize: 10,
  params: {}
}

// 商品状态枚举
enum ItemStatusEnum {
  已下架,
  已上架,
  已售完
}

// 商品状态对应颜色枚举
enum ItemStatusColorEnum {
  red,
  green,
  gray
}

export default function Inventory() {
  const [inventoryList, setInventoryList] = useState<IInventory[]>([])
  const [kindAllList, setKindAllList] = useState<SelectType[]>([])
  const [total, setTotal] = useState(0)
  const [searchForm] = Form.useForm()

  const [pageQuery, setPageQuery] = useState<IPageQuery<SearchParams>>(initialPageQuery)

  // 获取库存列表
  const getInventoryList = async () => {
    const { data, total } = await getInventoryListAPI<SearchParams>(pageQuery)
    setInventoryList(data || [])
    setTotal(total || 0)
  }

  // 获取种类列表
  const getKindAllList = async () => {
    const { data } = await getKindAllListAPI()
    setKindAllList(data || [])
  }

  useEffect(() => {
    getInventoryList()
  }, [pageQuery])

  useEffect(() => {
    getKindAllList()
  }, [])

  // 设置查询参数
  const handleSearch = (params: SearchParams) => {
    setPageQuery({
      ...pageQuery,
      params
    })
  }

  // 重置查询参数
  const handleReset = () => {
    searchForm.resetFields()
    setPageQuery(initialPageQuery)
  }

  // 删除库存
  const onDeleteInventory = (id: number) => {
    Modal.confirm({
      title: '删除提示',
      icon: <ExclamationCircleOutlined />,
      content: '您确认删除该库存吗？',
      okText: '确认',
      cancelText: '取消',
      onOk: async () => {
        try {
          const res = await deleteInventoryAPI(id)
          message[res.code === 200 ? 'success' : 'error'](res.message)
          getInventoryList()
        } catch (err: any) {
          message.error(err.data.message || '删除失败！')
        }
      }
    })
  }

  const type = useRef('add')
  const goodsNameInputRef = useRef<InputRef>(null)
  const [addOrEditForm] = Form.useForm<IInventory>()
  const [isAddOrEditModalOpen, setIsAddOrEditModalOpen] = useState(false)

  // 打开新增或编辑库存模态框
  const showAddOrEditModal = (row?: IInventory) => {
    type.current = 'add'
    if (row?.id) {
      type.current = 'edit'
      addOrEditForm.setFieldsValue(row)
    }
    setIsAddOrEditModalOpen(() => {
      // 打开模态框输入框自动获得焦点
      setTimeout(() => {
        goodsNameInputRef.current?.focus({
          cursor: 'end'
        })
      }, 0)
      return true
    })
  }

  // 提交新增或编辑表单
  const handleAddOrEditFinish = async (inventoryInfo: IInventory) => {
    const res = await addOrEditInventoryAPI(inventoryInfo)
    if (res.code === 200) {
      message.success(res.message, 2)
      addOrEditForm.resetFields()
      setIsAddOrEditModalOpen(false)
      getInventoryList()
    } else {
      message.error(res.message)
    }
  }

  // 新增或编辑库存信息
  const handleAddOrEdit = async () => {
    // 校验表单
    const inventoryInfo = await addOrEditForm.validateFields()
    handleAddOrEditFinish(inventoryInfo)
  }

  // 取消添加或编辑
  const handleAddOrEditCancel = () => {
    setIsAddOrEditModalOpen(false)
  }

  // 关闭模态框
  const handleClosed = () => {
    addOrEditForm.resetFields()
  }

  // 分页
  const onPagination = (pageNum: number, pageSize: number) => {
    setPageQuery({
      ...pageQuery,
      pageNum,
      pageSize
    })
  }

  // 表格列配置
  const columns: ColumnsType<IInventory> = [
    {
      title: '种类',
      dataIndex: 'kindName',
      align: 'center'
    },
    {
      title: '商品编号',
      dataIndex: 'goodsCode',
      align: 'center'
    },
    {
      title: '图片',
      dataIndex: 'imgUrl',
      align: 'center',
      render: (imgUrl) => (
        <Image
          width={100}
          src={imgUrl || 'error'}
          fallback="data:image/png;base64,"
        />
      )
    },
    {
      title: '商品名称',
      dataIndex: 'goodsName',
      render: (goodsName, row) => (
        <Button type="link" key={row.id} onClick={() => showAddOrEditModal(row)}>
          {goodsName}
        </Button>
      ),
      align: 'center'
    },
    {
      title: '库存数量',
      dataIndex: 'quantity',
      render: (quantity, row) => (
        <Space>
          <QuantityControl type="reduce" onChange={handleAddOrEditFinish} row={row} />
          <span style={{ display: 'inline-block', minWidth: '50px' }}>{quantity}</span>
          <QuantityControl type="add" onChange={handleAddOrEditFinish} row={row} />
        </Space>
      ),
      align: 'center'
    },
    {
      title: '计量单位',
      dataIndex: 'unit',
      align: 'center'
    },
    {
      title: '成本价',
      dataIndex: 'costPrice',
      render: (costPrice) => <span>¥ {costPrice}</span>,
      align: 'center'
    },
    {
      title: '销售价',
      dataIndex: 'sellingPrice',
      render: (sellingPrice) => <span>¥ {sellingPrice}</span>,
      align: 'center'
    },
    {
      title: '存放位置',
      dataIndex: 'location',
      align: 'center'
    },
    {
      title: '最后更新',
      dataIndex: 'lastUpdated',
      align: 'center'
    },
    {
      title: '状态',
      dataIndex: 'status',
      render: (type) => <Tag color={ItemStatusColorEnum[type]}>{ItemStatusEnum[type]}</Tag>,
      align: 'center'
    },
    {
      title: '操作',
      dataIndex: 'operation',
      render: (_, row) => (
        <Space>
          <Button type="primary" onClick={() => showAddOrEditModal(row)}>
            编辑
          </Button>
          <Button danger type="primary" onClick={() => onDeleteInventory(row.id!)}>
            删除
          </Button>
        </Space>
      ),
      align: 'center'
    }
  ]

  return (
    <div>
      {/* 搜索栏 */}
      <div className="search-bar">
        <Form name="search-form" form={searchForm} layout="inline" onFinish={handleSearch}>
          <Form.Item name="queryString">
            <Input placeholder="商品编号/商品名称/存放位置" style={{ width: 280 }} prefix={<SearchOutlined />} />
          </Form.Item>
          <Form.Item name="status">
            <Select
              placeholder="状态"
              className="w120"
              allowClear
              options={[
                {
                  value: 0,
                  label: '已下架'
                },
                {
                  value: 1,
                  label: '已上架'
                },
                {
                  value: 2,
                  label: '已售空'
                }
              ]}
            />
          </Form.Item>
          <Form.Item name="kindId">
            <Select placeholder="种类" className="w120" allowClear options={kindAllList} />
          </Form.Item>
          <Form.Item>
            <Space>
              <Button type="primary" htmlType="submit">
                查询
              </Button>
              <Button onClick={handleReset}>重置</Button>
              <Button onClick={() => showAddOrEditModal()}>新增</Button>
              <Modal
                title={type.current === 'add' ? '新增' : '编辑'}
                open={isAddOrEditModalOpen}
                onOk={() => handleAddOrEdit()}
                onCancel={handleAddOrEditCancel}
                afterClose={handleClosed}
                forceRender={true}
              >
                <Form
                  name="add-or-edit-form"
                  form={addOrEditForm}
                  labelCol={{ span: 4 }}
                  wrapperCol={{ span: 20 }}
                  autoComplete="off"
                  initialValues={{ isValid: true }}
                  onFinish={handleAddOrEditFinish}
                >
                  <Form.Item name="id" hidden>
                    <Input />
                  </Form.Item>
                  <Form.Item label="商品编号" name="goodsCode" hidden={type.current === 'add'}>
                    <Input disabled />
                  </Form.Item>
                  <Form.Item label="商品名称" name="goodsName" rules={[{ required: true, message: '请输入商品名称!' }]}>
                    <Input ref={goodsNameInputRef} />
                  </Form.Item>
                  <Form.Item label="库存数量" name="quantity">
                    <Input />
                  </Form.Item>
                  <Form.Item label="计量单位" name="unit">
                    <Input />
                  </Form.Item>
                  <Form.Item label="成本价" name="costPrice">
                    <InputNumber addonBefore="¥" precision={2} className="w120" />
                  </Form.Item>
                  <Form.Item label="销售价" name="sellingPrice">
                    <InputNumber addonBefore="¥" precision={2} className="w120" />
                  </Form.Item>
                  <Form.Item label="存放位置" name="location">
                    <Input />
                  </Form.Item>
                  <Form.Item label="状态" name="status">
                    <Select
                      className="w120"
                      allowClear
                      options={[
                        {
                          value: 0,
                          label: '已下架'
                        },
                        {
                          value: 1,
                          label: '已上架'
                        },
                        {
                          value: 2,
                          label: '已售完'
                        }
                      ]}
                    />
                  </Form.Item>
                  {/* 按钮仅用于触发表单的回车提交事件，可用 hidden 属性隐藏 */}
                  <button type="submit" hidden></button>
                </Form>
              </Modal>
            </Space>
          </Form.Item>
        </Form>
      </div>
      {/* 表格 */}
      <Table
        columns={columns}
        dataSource={inventoryList}
        rowKey="id"
        scroll={{ y: 660 }}
        pagination={{
          showSizeChanger: true,
          showQuickJumper: true,
          total: total,
          showTotal: (total) => `总 ${total} 条`,
          defaultPageSize: 10,
          defaultCurrent: 1,
          onChange: onPagination
        }}
      />
    </div>
  )
}
